import React, { useContext } from 'react';
import { Droppable, Draggable } from 'react-beautiful-dnd';
import Task from './Task';
import TaskContext from './TaskContext';

function TaskList() {
  const { tasks, setTasks, columnIds, setColumnIds } = useContext(TaskContext);

  const onDragEnd = (result) => {
    const { destination, source } = result;
    if (!destination) return;

    // Reorder tasks
    const newTasks = Array.from(tasks);
    const [removed] = newTasks.splice(source.index, 1);
    newTasks.splice(destination.index, 0, removed);
    setTasks(newTasks);

    // Reorder columnIds if necessary
    if (source.droppableId !== destination.droppableId) {
      const newColumnIds = Array.from(columnIds);
      newColumnIds.splice(source.index, 1);
      newColumnIds.splice(destination.index, 0, source.droppableId);
      setColumnIds(newColumnIds);
    }
  };

  return (
    <Droppable droppableId="taskList" direction="horizontal" type="TASK">
      {(provided, snapshot) => (
        <div
          ref={provided.innerRef}
          {...provided.droppableProps}
          style={{
            background: snapshot.isDraggingOver ? 'lightblue' : 'lightgrey',
            display: 'flex',
            padding: 8,
            overflowX: 'auto',
            minHeight: 200,
          }}
        >
          {tasks.map((task, index) => (
            <Draggable key={task.id} draggableId={task.id} index={index}>
              {(provided, snapshot) => (
                <div
                  ref={provided.innerRef}
                  {...provided.draggableProps}
                  {...provided.dragHandleProps}
                  style={{
                    ...provided.draggableProps.style,
                    margin: 4,
                    userSelect: 'none',
                    background: snapshot.isDragging ? '#263B4A' : '#456C86',
                    color: 'white',
                    borderRadius: 4,
                    ...provided.draggableProps.style,
                  }}
                >
                  <Task task={task} />
                </div>
              )}
            </Draggable>
          ))}
          {provided.placeholder}
        </div>
      )}
    </Droppable>
  );
}

export default TaskList;
