import React, { useContext, useState } from 'react';
import Task from './Task';
import TaskContext from './TaskContext';
import { useAuth } from './AuthContext';
import { useDrop } from 'react-dnd';

function TaskList() {
  const { tasks, setTasks } = useContext(TaskContext);
  const { user } = useAuth();
  const [draggedTaskId, setDraggedTaskId] = useState(null);

  const userTasks = tasks.filter(task => task.assigned_users.some(assignedUser => assignedUser.id === user.id));

  const handleDrop = (draggedId, targetIndex) => {
    const updatedTasks = [...userTasks];
    const draggedTask = updatedTasks.find(task => task.id === draggedId);
    const draggedIndex = updatedTasks.indexOf(draggedTask);

    // Determine the direction of movement
    const direction = targetIndex > draggedIndex ? 1 : -1;

    // Move the dragged task to the target index
    updatedTasks.splice(targetIndex, 0, draggedTask);

    // Remove the dragged task from its original position
    const originalIndex = updatedTasks.indexOf(draggedTask);
    updatedTasks.splice(originalIndex + (direction === 1 ? -1 : 1), 1);

    setTasks(updatedTasks);
  };


  const [{ isOver }, drop] = useDrop({
    accept: 'TASK',
    drop: (item, monitor) => {
      const draggedId = item.id;
      const targetIndex = userTasks.length; // Dropping at the end of the list
      handleDrop(draggedId, targetIndex);
    },
    collect: monitor => ({
      isOver: !!monitor.isOver(),
    }),
  });

  return (
    <div ref={drop} style={{ minHeight: '100vh', background: isOver ? 'lightblue' : 'white' }}>
      {userTasks.map((task, index) => (
        <Task
          key={task.id}
          index={index}
          task={task}
          sourceSection={task.section}
          isAdmin={user.role === 'admin'}
          setDraggedTaskId={setDraggedTaskId}
          handleDrop={handleDrop}
          draggedTaskId={draggedTaskId}
        />
      ))}
    </div>
  );
}

export default TaskList;
