import React, { useContext } from 'react';
import Task from './Task';
import TaskContext from './TaskContext';
import { useAuth } from './AuthContext';

function TaskList({ onDrop }) {
  const { tasks, setTasks, sourceSection } = useContext(TaskContext);
  const { user } = useAuth();
console.log(user.role)
const userTasks = tasks.filter(task => task.assigned_users.some(assignedUser => assignedUser.id === user.id));

  const handleDrop = (e) => {
    e.preventDefault();

    try {
      const droppedTaskData = e.dataTransfer.getData('task');

      if (!droppedTaskData) {
        // Handle empty data
        console.error("Error: Empty dropped task data.");
        return;
      }

      const droppedTask = JSON.parse(droppedTaskData);
      // Proceed with handling the dropped task
      onDrop(droppedTask);
    } catch (error) {
      console.error("Error parsing dropped task data:", error);
    }
  };


  const allowDrop = (e) => {
    e.preventDefault();
  };

  const moveTask = (dragIndex, hoverIndex, sourceTask, targetTask) => {
    // Log source task and target task to ensure they are correct
    console.log("Source Task:", sourceTask);
    console.log("Target Task:", targetTask);

    // Determine the direction of movement
    const direction = dragIndex < hoverIndex ? 'right' : 'left';

    // Create a copy of the tasks arrays for source and target tasks
    const updatedSourceTasks = tasks.map(task => sourceTask && task.id === sourceTask.id ? sourceTask : task);
    const updatedTargetTasks = tasks.map(task => targetTask && task.id === targetTask.id ? targetTask : task);


    // Get the dragged task
    const draggedTask = updatedSourceTasks[dragIndex];

    // Remove the task from the source tasks
    updatedSourceTasks.splice(dragIndex, 1);

    // Insert the task into the target tasks at the hover index
    updatedTargetTasks.splice(hoverIndex, 0, draggedTask);

    // Update the tasks array in state
    setTasks([...updatedSourceTasks, ...updatedTargetTasks]);

    // Handle horizontal movement
    if (direction === 'left' || direction === 'right') {
      // If moving right (forward)
      if (direction === 'right') {
        // Check if the hoverIndex is the last index in the target tasks
        if (hoverIndex === updatedTargetTasks.length - 1) {
          // Perform necessary actions when moving right
          console.log('Moving right...');
        }
      }
      // If moving left (backward)
      else {
        // Check if the hoverIndex is the first index in the target tasks
        if (hoverIndex === 0) {
          // Perform necessary actions when moving left
          console.log('Moving left...');
        }
      }
    }
  };

  console.log('Tasks:', tasks);

  const handleDragStart = (e, task) => {
    // Convert task data to a string using JSON.stringify and set it as 'task' format
    const taskDataString = JSON.stringify(task);
    console.log('Task data being set:', taskDataString); // Log the task data being set
    e.dataTransfer.setData('task', taskDataString); // Set the task data
};




  return (
    <div onDrop={handleDrop} onDragOver={allowDrop}>
      {userTasks.map((task, index) => (
      <Task
      key={task.id}
      index={index}
      task={task}
      moveTask={(dragIndex, hoverIndex) => moveTask(dragIndex, hoverIndex, sourceSection, task.section)} // Pass sourceSection dynamically
      sourceSection={sourceSection} // Pass sourceSection as a prop
      isAdmin={user.role === 'admin'}
      onDragStart={(e) => handleDragStart(e, task)} 
    />


      ))}
    </div>
  );
}


export default TaskList;
