import React, { useState } from 'react';
import { useDrag, useDrop } from 'react-dnd';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import { ItemTypes } from './ItemTypes';
import { useAuth } from './AuthContext';
import TaskContext from './TaskContext';

function Task({ task, index, moveTask }) {
  const [{ isDragging }, drag] = useDrag({
    type: ItemTypes.TASK,
    item: { type: ItemTypes.TASK, task, index },
    collect: monitor => ({
      isDragging: monitor.isDragging(),
    }),
  });

  const [, drop] = useDrop({
    accept: ItemTypes.TASK,
    hover(item, monitor) {
      const dragIndex = item.index;
      const hoverIndex = index;

      if (dragIndex === hoverIndex) {
        return;
      }

      moveTask(dragIndex, hoverIndex, sourceSection, 'targetSection'); // Use sourceSection prop
      item.index = hoverIndex;

      // Update sourceSection in TaskContext
      setSourceSection(sourceSection); // Set sourceSection when hovering over the task
    },
  });


  const opacity = isDragging ? 0.5 : 1;

  const [showModal, setShowModal] = useState(false);
  const [taskName, setTaskName] = useState(task.name);
  const [dateDue, setDateDue] = useState(task.due_date || '');
  const [priority, setPriority] = useState(task.priority);
  const [participants, setParticipants] = useState(task.assigned_users.map(user => user.id));
  const [selectedParticipants, setSelectedParticipants] = useState([]);
  const { user } = useAuth();
  const { setSourceSection } = useContext(TaskContext);

  const handlePriorityChange = (e) => {
    setPriority(e.target.value);
  };

  const handleParticipantChange = (e) => {
    const participantId = e.target.value;
    if (e.target.checked) {
      setSelectedParticipants([...selectedParticipants, participantId]);
    } else {
      setSelectedParticipants(selectedParticipants.filter(id => id !== participantId));
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleShowModal = () => {
    setShowModal(true);
  };

  const handleSaveChanges = async () => {
    const updatedTaskData = {
      name: taskName,
      due_date: dateDue,
      priority: priority,
      assigned_users: participants.filter(participant => selectedParticipants.includes(participant.id)),
      // Add other task properties here
    };

    try {
      const response = await fetch(`your_api_endpoint/tasks/${task.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedTaskData),
      });
      if (response.ok) {
        console.log("Task updated successfully:", task.id);
        setShowModal(false); // Close the modal after successful update
        // Optionally, you can update your state to reflect the update
      } else {
        console.error("Error updating task:", response.statusText);
      }
    } catch (error) {
      console.error("Error updating task:", error);
    }
  };

  return (
    <>
        <div ref={(node) => drag(drop(node))} style={{ opacity }}>
        <Card>
          <Card.Body>
            <Card.Title>{task.name}</Card.Title>
            <Card.Text>
              <strong>Due Date:</strong> {task.due_date || 'No due date'}
              <br />
              <strong>Priority:</strong> {task.priority}
              <br />
              <strong>Assigned Users:</strong> {task.assigned_users.map(user => user.fullName).join(', ')}
            </Card.Text>
            {user && user.role === 'admin' && (
              <Button variant="primary" onClick={handleShowModal}>Open Modal</Button>
            )}
          </Card.Body>
        </Card>
      </div>

      {/* Update Task Modal */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Update Task</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <fieldset>
              <Form.Group className="mb-3">
                <Form.Label htmlFor="taskName">Task Name</Form.Label>
                <Form.Control id="taskName" placeholder="Task Name" value={taskName} onChange={e => setTaskName(e.target.value)} />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label htmlFor="dateDue">Date Due</Form.Label>
                <Form.Control id="dateDue" type="date" placeholder="Date Due" value={dateDue} onChange={e => setDateDue(e.target.value)} />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label htmlFor="priority">Priority</Form.Label>
                <Form.Select id="priority" onChange={handlePriorityChange} value={priority}>
                  <option value="">Select Priority</option>
                  <option value="High">High</option>
                  <option value="Medium">Medium</option>
                  <option value="Low">Low</option>
                </Form.Select>
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Participants</Form.Label>
                {participants.map(participant => (
                  <div key={participant.id}>
                    <Form.Check
                      type="checkbox"
                      id={`participant-${participant.id}`}
                      label={`${participant.username} - ${participant.fullName}`}
                      value={participant.id}
                      checked={selectedParticipants.includes(participant.id)}
                      onChange={handleParticipantChange}
                    />
                  </div>
                ))}
              </Form.Group>
            </fieldset>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>Close</Button>
          <Button variant="primary" onClick={handleSaveChanges}>Save Changes</Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default Task;
