import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import EventCard from './EventCard';
import TaskList from './TaskList';
import { v4 as uuidv4 } from 'uuid'; // Import uuid

const Dashboard = () => {
  const [columns, setColumns] = useState({
    todo: {
      title: 'ToDo',
      items: [
        { id: 'task1', title: 'Task 1' },
        { id: 'task2', title: 'Task 2' },
        { id: 'task3', title: 'Task 3' }
      ]
    },
    inProgress: {
      id: 'inProgress', // Static key for InProgress column
      title: 'InProgress',
      items: []
    },
    completed: {
      id: 'completed', // Static key for Completed column
      title: 'Completed',
      items: []
    },
    delayed: {
      id: 'delayed', // Static key for Delayed column
      title: 'Delayed',
      items: []
    }
  });

  const [renderedTasks, setRenderedTasks] = useState({});

  const onDragEnd = result => {
    const { destination, source } = result;

    if (!destination) {
      return;
    }

    if (destination.droppableId === source.droppableId && destination.index === source.index) {
      return;
    }

    const startColumn = columns[source.droppableId];
    const finishColumn = columns[destination.droppableId];
    const startItems = [...startColumn.items];
    const finishItems = [...finishColumn.items];
    const [draggedItem] = startItems.splice(source.index, 1);
    finishItems.splice(destination.index, 0, draggedItem);

    setColumns({
      ...columns,
      [source.droppableId]: { ...startColumn, items: startItems },
      [destination.droppableId]: { ...finishColumn, items: finishItems }
    });
  };

  const renderTask = (taskId) => {
    if (!renderedTasks[taskId]) {
      setRenderedTasks({
        ...renderedTasks,
        [taskId]: true
      });
      return true;
    }
    return false;
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="dashboard">
        <aside className="sidebar">
          <h2>Upcoming Events</h2>
          <EventCard />
        </aside>
        <div className="main-section">
          <main className="main-content" style={{ height: "fit-content", width: "100%" }}>
            <h1>Main Content</h1>
            <p>This is the main content area of the dashboard.</p>
          </main>
          <div style={{ display: 'flex' }}>
            {Object.keys(columns).map(columnId => (
              <Droppable droppableId={`droppable-${columnId}`} key={columnId}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    style={{
                      backgroundColor: 'lightgrey',
                      flex: '1',
                      margin: '8px',
                      minHeight: '200px'
                    }}
                  >
                    <div className="column">
                      <div className="column-section">
                        <h2>{columns[columnId].title}</h2>
                        {columns[columnId].items.map((item, index) => (
                          renderTask(item.id) &&
                          <Draggable key={item.id} draggableId={item.id} index={index}>
                            {(provided) => (
                              <div
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                              >
                                <TaskList task={item} />
                              </div>
                            )}
                          </Draggable>
                        ))}

                        {provided.placeholder}
                      </div>
                    </div>
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </div>
      </div>
    </DragDropContext>
  );
};

export default Dashboard;
