import React, { useContext } from 'react';
import Task from './Task';
import TaskContext from './TaskContext';
import { useAuth } from './AuthContext';

function TaskList({ onDrop }) {
  const { tasks, setTasks, sourceSection } = useContext(TaskContext);
  const { user } = useAuth();
console.log(user.role)
const userTasks = tasks.filter(task => task.assigned_users.some(assignedUser => assignedUser.id === user.id));

  const handleDrop = (e) => {
    e.preventDefault();

    try {
      const droppedTaskData = e.dataTransfer.getData('task');

      if (!droppedTaskData) {
        // Handle empty data
        console.error("Error: Empty dropped task data.");
        return;
      }

      const droppedTask = JSON.parse(droppedTaskData);
      // Proceed with handling the dropped task
      onDrop(droppedTask);
    } catch (error) {
      console.error("Error parsing dropped task data:", error);
    }
  };


  const allowDrop = (e) => {
    e.preventDefault();
  };

  const moveTask = (dragIndex, hoverIndex, sourceSection, targetSection) => {
    // Ensure source and target sections are valid
    if (!tasks[sourceSection] || !tasks[targetSection]) {
      console.error("Error: Source or target section is not defined or does not exist.");
      return;
    }

    // Determine the direction of movement
    const direction = dragIndex < hoverIndex ? 'right' : 'left';

    // Get the dragged task
    const draggedTask = tasks[sourceSection][dragIndex];

    // If the source and target sections are the same, perform in-place reordering
    if (sourceSection === targetSection) {
      // Remove the dragged task from its current position
      const updatedTasks = [...tasks[sourceSection]];
      updatedTasks.splice(dragIndex, 1);

      // Insert the dragged task at the new position
      updatedTasks.splice(hoverIndex, 0, draggedTask);

      // Update the tasks array in state
      setTasks({
        ...tasks,
        [sourceSection]: updatedTasks
      });
    } else {
      // If the source and target sections are different, move the task between sections
      const updatedSourceTasks = [...tasks[sourceSection]];
      const updatedTargetTasks = [...tasks[targetSection]];

      // Remove the dragged task from the source section
      updatedSourceTasks.splice(dragIndex, 1);

      // Insert the dragged task into the target section at the hover index
      updatedTargetTasks.splice(hoverIndex, 0, draggedTask);

      // Update the tasks arrays in state
      setTasks({
        ...tasks,
        [sourceSection]: updatedSourceTasks,
        [targetSection]: updatedTargetTasks
      });
    }

    // Handle horizontal movement
    if (direction === 'left' || direction === 'right') {
      // If moving right (forward)
      if (direction === 'right') {
        // Check if the hoverIndex is the last index in the target section
        if (hoverIndex === tasks[targetSection].length - 1) {
          // Perform necessary actions when moving right
          console.log('Moving right...');
        }
      }
      // If moving left (backward)
      else {
        // Check if the hoverIndex is the first index in the target section
        if (hoverIndex === 0) {
          // Perform necessary actions when moving left
          console.log('Moving left...');
        }
      }
    }
  };


  console.log('Tasks:', tasks);

  const handleDragStart = (e, task) => {
    // Convert task data to a string using JSON.stringify and set it as 'task' format
    const taskDataString = JSON.stringify(task);
    console.log('Task data being set:', taskDataString); // Log the task data being set
    e.dataTransfer.setData('task', taskDataString); // Set the task data
};




  return (
    <div onDrop={handleDrop} onDragOver={allowDrop}>
      {userTasks.map((task, index) => (
      <Task
      key={task.id}
      index={index}
      task={task}
      moveTask={(dragIndex, hoverIndex) => moveTask(dragIndex, hoverIndex, sourceSection, task.section)} // Pass sourceSection dynamically
      sourceSection={sourceSection} // Pass sourceSection as a prop
      isAdmin={user.role === 'admin'}
      onDragStart={(e) => handleDragStart(e, task)} 
    />


      ))}
    </div>
  );
}


export default TaskList;
