import React, { useState } from 'react';
import '../index.css'; // Assuming you have a separate CSS file for styles
import EventCard from './EventCard';
import TaskList from './TaskList';

function Dashboard() {
  const [toDoTasks, setToDoTasks] = useState([]);
  const [inProgressTasks, setInProgressTasks] = useState([]);
  const [delayedTasks, setDelayedTasks] = useState([]);

  const handleDrop = (status, index, task) => {
    if (status === 'toDo') {
      // handleDrop logic for ToDo tasks
    } else if (status === 'inProgress') {
      // handleDrop logic for InProgress tasks
    } else if (status === 'delayed') {
      // handleDrop logic for Delayed tasks
    }
  };

  return (
    <div className="dashboard">
      <aside className="sidebar">
        <h2>Upcoming Events</h2>
        <EventCard />
      </aside>
      <div className="separator"></div>
      <div className="column">
        <div className="column-section">
          <h2>ToDo</h2>
          <TaskList tasks={toDoTasks} onDrop={(index, task) => handleDrop('toDo', index, task)} />
        </div>

        <div className="column-section">
          <h2>InProgress</h2>
          {inProgressTasks.length > 0 ? (
            <TaskList tasks={inProgressTasks} onDrop={(index, task) => handleDrop('inProgress', index, task)} />
          ) : (
            <p>No tasks in progress</p>
          )}
        </div>

        <div className="column-section">
          <h2>Delayed</h2>
          {delayedTasks.length > 0 ? (
            <TaskList tasks={delayedTasks} onDrop={(index, task) => handleDrop('delayed', index, task)} />
          ) : (
            <p>No delayed tasks</p>
          )}
        </div>
      </div>
      <main className="main-content">
        <h1>Main Content</h1>
        <p>This is the main content area of the dashboard.</p>
      </main>
    </div>
  );
}

export default Dashboard;
