import React, { useContext, useEffect, useState } from 'react';
import { Droppable, Draggable } from 'react-beautiful-dnd';
import Task from './Task';
import TaskContext from './TaskContext';

function TaskList({ droppableId }) {
  const { tasks, setTasks, columnIds, setColumnIds } = useContext(TaskContext);
  const [delayedDestination, setDelayedDestination] = useState(null);

  useEffect(() => {
    if (delayedDestination) {
      setTimeout(() => {
        setColumnIds(delayedDestination);
      }, 300); // Adjust the delay time as needed
    }
  }, [delayedDestination, setColumnIds]);

  const onDragEnd = (result) => {
    const { destination, source } = result;
    if (!destination) return;

    const newTasks = Array.from(tasks);
    const [removed] = newTasks.splice(source.index, 1);
    newTasks.splice(destination.index, 0, removed);
    setTasks(newTasks);
    setDelayedDestination(destination.droppableId); // Store destination ID after drag end
  };
  console.log(columnIds)

  return (
    <Droppable droppableId={String(columnIds)} direction="horizontal">
      {(provided) => (
        <div ref={provided.innerRef} {...provided.droppableProps}>
          {tasks.map((task, index) => (
            <Draggable key={String(task.id)} draggableId={String(task.id)} index={index}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.draggableProps}
                  {...provided.dragHandleProps}
                >
                  <Task task={task} />
                </div>
              )}
            </Draggable>
          ))}
          {provided.placeholder}
        </div>
      )}
    </Droppable>
  );
}

export default TaskList;
