import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import InProgressTaskList from './InProgressTaskList';
import DelayedTaskList from './DelayedTaskList';
import CompletedTaskList from './CompletedTaskList';
import EventCard from './EventCard';
import TaskList from './TaskList';
import TaskContext from './TaskContext'; // Import TaskContext
import { v4 as uuidv4 } from 'uuid'; // Import uuid

const Dashboard = () => {
  const [columns, setColumns] = useState({
    todo: {
      title: 'ToDo',
      items: [
        { id: uuidv4(), title: 'Task 1' },
        { id: uuidv4(), title: 'Task 2' },
        { id: uuidv4(), title: 'Task 3' }
        // Add more initial tasks here if needed
      ]
    },
    inProgress: {
      title: 'InProgress',
      items: []
    },
    completed: {
      title: 'Completed',
      items: []
    },
    delayed: {
      title: 'Delayed',
      items: []
    }
  });


  const onDragEnd = result => {
    const { destination, source, draggableId } = result;

    if (!destination) {
      return;
    }

    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      return;
    }

    const start = columns[source.droppableId];
    const finish = columns[destination.droppableId];

    if (start === finish) {
      const newItems = Array.from(start.items);
      const [removed] = newItems.splice(source.index, 1);
      newItems.splice(destination.index, 0, removed);

      const newColumn = {
        ...start,
        items: newItems
      };

      setColumns({
        ...columns,
        [source.droppableId]: newColumn
      });

      return;
    }

    const startItems = Array.from(start.items);
    const [removed] = startItems.splice(source.index, 1);
    const newStart = {
      ...start,
      items: startItems
    };

    const finishItems = Array.from(finish.items);
    finishItems.splice(destination.index, 0, removed);
    const newFinish = {
      ...finish,
      items: finishItems
    };

    setColumns({
      ...columns,
      [source.droppableId]: newStart,
      [destination.droppableId]: newFinish
    });
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="dashboard">
        <aside className="sidebar">
          <h2>Upcoming Events</h2>
          <EventCard />
        </aside>
        <div className="main-section">
          <main className="main-content" style={{ height: "fit-content", width: "100%" }}>
            <h1>Main Content</h1>
            <p>This is the main content area of the dashboard.</p>
          </main>
          <div style={{ display: 'flex' }}>
            {Object.keys(columns).map(columnId => {
              const column = columns[columnId];
              return (
                <Droppable droppableId={columnId} key={columnId}>
                  {(provided) => (
                    <div ref={provided.innerRef} {...provided.droppableProps} style={{ backgroundColor: 'lightgrey', flex: '1' }}>
                      <div className="column">
                        <div className="column-section">
                          <h2>{column.title}</h2>
                          {column.items.map((item, index) => (
                            <TaskList key={item.id} index={index} task={item} />
                          ))}
                          {provided.placeholder}
                        </div>
                      </div>
                    </div>
                  )}
                </Droppable>
              );
            })}
          </div>
        </div>
      </div>
    </DragDropContext>
  );
};

export default Dashboard;
