import React, { useState } from 'react';
import '../index.css'; // Assuming you have a separate CSS file for styles
import EventCard from './EventCard'; // Corrected import statement
import TaskList from './TaskList';

function Dashboard() {
  const [toDoTasks, setToDoTasks] = useState([]);
  const [inProgressTasks, setInProgressTasks] = useState([]);
  const [delayedTasks, setDelayedTasks] = useState([]);

  const handleToDoDrop = (task) => {
    if (task.fromSection === 'ToDo') {
      if (task.status === 'inProgress') {
        setInProgressTasks((prevTasks) => [...prevTasks, task]);
        setToDoTasks((prevTasks) => prevTasks.filter((t) => t.id !== task.id));
      } else if (task.status === 'delayed') {
        setDelayedTasks((prevTasks) => [...prevTasks, task]);
        setToDoTasks((prevTasks) => prevTasks.filter((t) => t.id !== task.id));
      }
    }
  };

  return (
    <div className="dashboard">
      <aside className="sidebar">
        <h2>Upcoming Events</h2>
        <EventCard />
      </aside>
      <div className="separator"></div>
      <div className="column">
        <div className="column-section">
          <h2>ToDo</h2>
          <TaskList tasks={toDoTasks} onDrop={(task) => handleToDoDrop({...task, fromSection: 'ToDo'})} />
        </div>

        <div className="column-section">
          <h2>InProgress</h2>
          <TaskList tasks={inProgressTasks} />
        </div>

        <div className="column-section">
          <h2>Delayed</h2>
          <TaskList tasks={delayedTasks} />
        </div>
      </div>
      <main className="main-content">
        <h1>Main Content</h1>
        <p>This is the main content area of the dashboard.</p>
      </main>
    </div>
  );
}

export default Dashboard;
