// In TaskList.js
import React, { useContext } from 'react';
import Task from './Task';
import TaskContext from './TaskContext';
import { useAuth } from './AuthContext';

function TaskList({ onDrop }) {
  const { tasks, setTasks, sourceSection, setSourceSection } = useContext(TaskContext);
  const { user } = useAuth();

  const userTasks = tasks.filter(task => task.assigned_users.some(assignedUser => assignedUser.id === user.id));

  const handleDrop = (e) => {
    e.preventDefault();
    try {
      const droppedTaskData = e.dataTransfer.getData('task');
      if (!droppedTaskData) {
        console.error("Error: Empty dropped task data.");
        return;
      }
      const droppedTask = JSON.parse(droppedTaskData);
      onDrop(droppedTask);
    } catch (error) {
      console.error("Error parsing dropped task data:", error);
    }
  };

  const allowDrop = (e) => {
    e.preventDefault();
  };


  const moveTask = (dragIndex, hoverIndex, sourceSection, targetSection) => {
    if (!tasks[sourceSection] || !tasks[targetSection]) {
      console.error("Error: Source or target section is not defined or does not exist.");
      return;
    }

    const updatedSourceTasks = [...tasks[sourceSection]]; // Create a copy of source tasks
    const draggedTask = updatedSourceTasks.splice(dragIndex, 1)[0]; // Remove dragged task

    const updatedTargetTasks = [...tasks[targetSection]]; // Create a copy of target tasks
    updatedTargetTasks.splice(hoverIndex, 0, draggedTask); // Insert dragged task

    setTasks({
      ...tasks,
      [sourceSection]: updatedSourceTasks,
      [targetSection]: updatedTargetTasks
    });

    const direction = dragIndex < hoverIndex ? 'right' : 'left';
    if (direction === 'right') {
      console.log('Moving right...');
    } else {
      console.log('Moving left...');
    }
  };

  const handleDragStart = (e, task) => {
    const taskDataString = JSON.stringify(task);
    e.dataTransfer.setData('task', taskDataString);
  };

  return (
    <div onDrop={handleDrop} onDragOver={allowDrop}>
      {userTasks.map((task, index) => (
      <Task
      key={task.id}
      index={index}
      task={task}
      moveTask={(dragIndex, hoverIndex) => moveTask(dragIndex, hoverIndex, task.section, sourceSection)} // Pass sourceSection dynamically
      sourceSection={sourceSection} // Pass sourceSection as a prop
      isAdmin={user.role === 'admin'}
      onDragStart={(e) => handleDragStart(e, task)} 
    />

      ))}
    </div>
  );
}

export default TaskList;
