import React, { useContext } from 'react';
import Task from './Task';
import TaskContext from './TaskContext';
import { useAuth } from './AuthContext';

function TaskList({ onDrop }) {
    const { tasks, setTasks } = useContext(TaskContext);
    const { user } = useAuth();

    const handleDrop = (e) => {
      e.preventDefault();

      try {
        const droppedTaskData = e.dataTransfer.getData('task');
        if (!droppedTaskData) {
          // Handle empty data
          console.error("Error: Empty dropped task data.");
          return;
        }

        const droppedTask = JSON.parse(droppedTaskData);
        // Proceed with handling the dropped task
        onDrop(droppedTask);
      } catch (error) {
        console.error("Error parsing dropped task data:", error);
      }
    };

    const allowDrop = (e) => {
      e.preventDefault();
    };

    const moveTask = (dragIndex, hoverIndex, sourceSection, targetSection) => {
        // Create a copy of the tasks arrays for source and target sections
        const updatedSourceTasks = [...tasks[sourceSection]];
        const updatedTargetTasks = [...tasks[targetSection]];

        // Get the dragged task
        const draggedTask = updatedSourceTasks[dragIndex];

        // Remove the task from the source section
        updatedSourceTasks.splice(dragIndex, 1);

        // Insert the task into the target section at the hover index
        updatedTargetTasks.splice(hoverIndex, 0, draggedTask);

        // Update the tasks arrays in state
        setTasks({
          ...tasks,
          [sourceSection]: updatedSourceTasks,
          [targetSection]: updatedTargetTasks
        });
      };

      return (
        <div onDrop={handleDrop} onDragOver={allowDrop}>
          {Object.entries(tasks).map(([section, sectionTasks]) => {
            console.log(sectionTasks); // Add this line
            return (
              <div key={section}>
                <h2>{section}</h2>
                {/* Convert sectionTasks into an array using Object.values() */}
                {Object.values(sectionTasks).map((task, index) => (
                  <Task
                    key={task.id}
                    index={index}
                    task={task}
                    moveTask={(dragIndex, hoverIndex) => moveTask(dragIndex, hoverIndex, section, /* pass the target section here */) }
                    isAdmin={user.role === 'admin'}
                  />
                ))}
              </div>
            );
          })}
        </div>
      );


  }


export default TaskList;
