// TaskList.js
import React from 'react';
import { useDrag, useDrop } from 'react-dnd';
import Card from 'react-bootstrap/Card';
import { ItemTypes } from './ItemTypes'; // Assuming ItemTypes is defined in a separate file

function Task({ task, index, moveTask }) {
  const [{ isDragging }, drag] = useDrag({
    type: ItemTypes.TASK,
    item: { type: ItemTypes.TASK, task, index },
    collect: monitor => ({
      isDragging: !!monitor.isDragging(),
    }),
  });

  const [, drop] = useDrop({
    accept: ItemTypes.TASK,
    hover() {},
    drop: (item) => {
      moveTask(item.index, index, item.task);
    },
  });

  const ref = React.useRef(null);
  drag(drop(ref));

  return (
    <div
      ref={ref}
      style={{ opacity: isDragging ? 0.5 : 1 }}
      role="listitem"
    >
      <Card>
        <Card.Body>
          <Card.Title>{task.name}</Card.Title>
          <Card.Text>
            <strong>Description:</strong> {task.description || 'No description'}
            <br />
            <strong>Due Date:</strong> {task.due_date || 'No due date'}
            <br />
            <strong>Priority:</strong> {task.priority}
            <br />
            <strong>Assigned Users:</strong> {task.assigned_users.map(user => user.fullName).join(', ')}
          </Card.Text>
        </Card.Body>
      </Card>
    </div>
  );
}

function TaskList({ tasks, onDrop }) {
  const moveTask = (dragIndex, hoverIndex, task) => {
    // Call the onDrop function passed from Dashboard with index and task
    onDrop(hoverIndex, task);
  };

  return (
    <div>
      {tasks.map((task, index) => (
        <Task key={task.id} index={index} task={task} moveTask={moveTask} />
      ))}
    </div>
  );
}

export default TaskList;
