import React, { useContext } from 'react';
import Task from './Task';
import TaskContext from './TaskContext';
import { useAuth } from './AuthContext';

function TaskList({ onDrop }) {
  const { tasks, setTasks } = useContext(TaskContext);
  const { user } = useAuth();

  const handleDrop = (e) => {
    e.preventDefault();

    try {
      const droppedTaskData = e.dataTransfer.getData('task');
      if (!droppedTaskData) {
        // Handle empty data
        console.error("Error: Empty dropped task data.");
        return;
      }

      const droppedTask = JSON.parse(droppedTaskData);
      // Proceed with handling the dropped task
      onDrop(droppedTask);
    } catch (error) {
      console.error("Error parsing dropped task data:", error);
    }
  };


  const allowDrop = (e) => {
    e.preventDefault();
  };

  const moveTask = (dragIndex, hoverIndex) => {
    // Create a copy of the tasks array to avoid mutating state directly
    const updatedTasks = [...tasks];

    // Get the dragged task
    const draggedTask = updatedTasks[dragIndex];

    // Remove the task from its original position
    updatedTasks.splice(dragIndex, 1);

    // Insert the task into its new position
    updatedTasks.splice(hoverIndex, 0, draggedTask);

    // Update the tasks array in state
    setTasks(updatedTasks);
  };



  return (
    <div onDrop={handleDrop} onDragOver={allowDrop}>
      {tasks.map((task, index) => (
        <Task
          key={task.id}
          index={index}
          task={task}
          moveTask={moveTask} // Pass moveTask as a prop to Task component
          isAdmin={user.role === 'admin'}
        />
      ))}
    </div>
  );
}

export default TaskList;
