// In TaskList.js
import React, { useContext } from 'react';
import Task from './Task';
import TaskContext from './TaskContext';
import { useAuth } from './AuthContext';

function TaskList({ onDrop }) {
  const { tasks, setTasks, sourceSection } = useContext(TaskContext);
  const { user } = useAuth();

  const userTasks = tasks.filter(task => task.assigned_users.some(assignedUser => assignedUser.id === user.id));

  const handleDrop = (e) => {
    e.preventDefault();
    try {
      const droppedTaskData = e.dataTransfer.getData('task');
      if (!droppedTaskData) {
        console.error("Error: Empty dropped task data.");
        return;
      }
      const droppedTask = JSON.parse(droppedTaskData);
      onDrop(droppedTask);
    } catch (error) {
      console.error("Error parsing dropped task data:", error);
    }
  };

  const allowDrop = (e) => {
    e.preventDefault();
  };

  const moveTask = (dragIndex, hoverIndex, targetSection) => {
    // Ensure source and target sections are valid
    if (!sourceSection || !targetSection) {
      console.error("Error: Source or target section is not defined or does not exist.");
      return;
    }

    // Get the dragged task
    const draggedTask = tasks.find(task => task.section === sourceSection)[dragIndex];

    // Find the index of the target task
    const targetIndex = tasks.findIndex(task => task.section === targetSection);

    // Make a shallow copy of the tasks array
    const newTasks = [...tasks];

    // Remove the dragged task from the source section
    const sourceTasks = newTasks.filter(task => task.section === sourceSection);
    const updatedSourceTasks = sourceTasks.filter((_, index) => index !== dragIndex);

    // Insert the dragged task into the target section at the hover index
    updatedSourceTasks.splice(hoverIndex, 0, draggedTask);

    // Update the tasks array with the modified source section
    newTasks.splice(targetIndex, sourceTasks.length, ...updatedSourceTasks);

    // Update the tasks array in state
    setTasks(newTasks);

    // Handle horizontal movement
    const direction = dragIndex < hoverIndex ? 'right' : 'left';
    if (direction === 'right') {
      console.log('Moving right...');
    } else {
      console.log('Moving left...');
    }
  };

  return (
    <div onDrop={handleDrop} onDragOver={allowDrop}>
      {userTasks.map((task, index) => (
        <Task
          key={task.id}
          index={index}
          task={task}
          moveTask={(dragIndex, hoverIndex) => moveTask(dragIndex, hoverIndex, task.section)} 
          sourceSection={task.section} // Pass sourceSection dynamically
          isAdmin={user.role === 'admin'}
        />
      ))}
    </div>
  );
}

export default TaskList;
