import React from 'react';
import { DndProvider, useDrag } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import '../index.css'; // Import your CSS file for styling

// Card component
const Card = ({ id, title, category, moveCard }) => {
    const [{ isDragging }, drag] = useDrag({
        type: 'CARD',
        item: { id, title, category },
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
    });

    return (
        <div ref={drag} className={`dashboard-card ${isDragging ? 'dragging' : ''}`}>
            {title}
        </div>
    );
};

class Dashboard extends React.Component {
    state = {
        upcomingEvents: [
            { id: 1, title: 'Meeting with Client', category: 'professional' },
            { id: 2, title: 'Birthday Party', category: 'social' },
            { id: 3, title: 'Project Deadline', category: 'professional' },
            { id: 4, title: 'Dinner with Friends', category: 'personal' },
            // Add more events as needed
        ],
        tasks: [
            { id: 1, title: 'Prepare presentation slides', category: 'professional' },
            { id: 2, title: 'Buy groceries', category: 'personal' },
            { id: 3, title: 'Call mom', category: 'personal' },
            // Add more tasks as needed
        ]
    };

    moveCard = (id, targetCategory, targetIndex) => {
        // Find the card object with the given id
        const cardToMove = this.state.upcomingEvents.find(card => card.id === id);
        // Remove the card from its current position
        const updatedEvents = this.state.upcomingEvents.filter(card => card.id !== id);
        // Insert the card at the target index within the same category
        updatedEvents.splice(targetIndex, 0, { ...cardToMove, category: targetCategory });
        // Update the state with the new order of cards
        this.setState({ upcomingEvents: updatedEvents });
    };

    render() {
        const { upcomingEvents, tasks } = this.state;

        return (
            <DndProvider backend={HTML5Backend}>
                <div className="dashboard-container">
                    <h2>Dashboard Overview</h2>
                    <div className="row">
                        <div className="col flex-column border-end" onDrop={(e) => {
                            const id = e.dataTransfer.getData("id");
                            // Get the target index based on the position where the card is dropped
                            const targetIndex = Number(e.target.getAttribute("data-index"));
                            this.moveCard(id, "all", targetIndex);
                        }} onDragOver={(e) => e.preventDefault()}>
                            <h3>All</h3>
                            {upcomingEvents.map((event, index) => (
                                <Card key={event.id} id={event.id} title={event.title} category={event.category} moveCard={this.moveCard} />
                            ))}
                            {tasks.map((task, index) => (
                                <Card key={task.id} id={task.id} title={task.title} category={task.category} moveCard={this.moveCard} />
                            ))}
                        </div>
                        <div className="col flex-column border-end" onDrop={(e) => {
                            const id = e.dataTransfer.getData("id");
                            const targetIndex = Number(e.target.getAttribute("data-index"));
                            this.moveCard(id, "personal", targetIndex);
                        }} onDragOver={(e) => e.preventDefault()}>
                            <h3>Personal</h3>
                            {upcomingEvents.map((event, index) => event.category === 'personal' && (
                                <Card key={event.id} id={event.id} title={event.title} category={event.category} moveCard={this.moveCard} />
                            ))}
                            {tasks.map((task, index) => task.category === 'personal' && (
                                <Card key={task.id} id={task.id} title={task.title} category={task.category} moveCard={this.moveCard} />
                            ))}
                        </div>
                        <div className="col flex-column border-end" onDrop={(e) => {
                            const id = e.dataTransfer.getData("id");
                            const targetIndex = Number(e.target.getAttribute("data-index"));
                            this.moveCard(id, "professional", targetIndex);
                        }} onDragOver={(e) => e.preventDefault()}>
                            <h3>Professional</h3>
                            {upcomingEvents.map((event, index) => event.category === 'professional' && (
                                <Card key={event.id} id={event.id} title={event.title} category={event.category} moveCard={this.moveCard} />
                            ))}
                            {tasks.map((task, index) => task.category === 'professional' && (
                                <Card key={task.id} id={task.id} title={task.title} category={task.category} moveCard={this.moveCard} />
                            ))}
                        </div>
                        <div className="col flex-column" onDrop={(e) => {
                            const id = e.dataTransfer.getData("id");
                            const targetIndex = Number(e.target.getAttribute("data-index"));
                            this.moveCard(id, "social", targetIndex);
                        }} onDragOver={(e) => e.preventDefault()}>
                            <h3>Social</h3>
                            {upcomingEvents.map((event, index) => event.category === 'social' && (
                                <Card key={event.id} id={event.id} title={event.title} category={event.category} moveCard={this.moveCard} />
                            ))}
                            {tasks.map((task, index) => task.category === 'social' && (
                                <Card key={task.id} id={task.id} title={task.title} category={task.category} moveCard={this.moveCard} />
                            ))}
                        </div>
                    </div>
                </div>
            </DndProvider>
        );
    }
}

export default Dashboard;
