import React, { useState, useEffect, useContext } from 'react';
import InProgressTaskList from './InProgressTaskList';
import DelayedTaskList from './DelayedTaskList';
import CompletedTaskList from './CompletedTaskList';
import EventCard from './EventCard';
import TaskList from './TaskList';
import TaskContext from './TaskContext'; // Import TaskContext

function Dashboard() {
  const [toDoTasks, setToDoTasks] = useState([]);
  const [inProgressTasks, setInProgressTasks] = useState([]);
  const [delayedTasks, setDelayedTasks] = useState([]);
  const [completedTasks, setCompletedTasks] = useState([]);
  const { sourceSection } = useContext(TaskContext); // Get sourceSection from TaskContext

  const handleToDoDrop = (index, task, event) => {
    if (event) {
      event.preventDefault();
      if (event.dataTransfer) {
        event.dataTransfer.dropEffect = 'move';
      }
    }

    console.log('Moving task to ToDo:', task);

    setToDoTasks(prevTasks => {
      const updatedTasks = [...prevTasks];
      const existingIndex = updatedTasks.findIndex(item => item.id === task.id);
      if (existingIndex !== -1) {
        updatedTasks.splice(existingIndex, 1);
      }
      updatedTasks.splice(index, 0, task);
      return updatedTasks;
    });
  };

  const handleInProgressDrop = (index, task) => {
    console.log('Moving task to InProgress:', task);

    setToDoTasks(prevTasks => prevTasks.filter((_, i) => i !== index));
    setDelayedTasks(prevTasks => prevTasks.filter((_, i) => i !== index));
    setInProgressTasks(prevTasks => [...prevTasks, task]);
  };

  const handleCompletedDrop = (index, task) => {
    console.log('Moving task to Completed:', task);

    setToDoTasks(prevTasks => prevTasks.filter((_, i) => i !== index));
    setInProgressTasks(prevTasks => prevTasks.filter((_, i) => i !== index));
    setDelayedTasks(prevTasks => prevTasks.filter((_, i) => i !== index));
    setCompletedTasks(prevTasks => [...prevTasks, task]);
  };

  const handleDelayedDrop = (index, task) => {
    console.log('Moving task to Delayed:', task);

    setToDoTasks(prevTasks => prevTasks.filter((_, i) => i !== index));
    setInProgressTasks(prevTasks => prevTasks.filter((_, i) => i !== index));
    setDelayedTasks(prevTasks => [...prevTasks, task]);
  };  

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDragEnter = (e) => {
    e.preventDefault();
    // Add your logic for handling drag enter event
  };

  const handleDragLeave = (e) => {
    // Add your logic for handling drag leave event
  };

  useEffect(() => {
    // Clear in progress, delayed, and completed tasks when toDoTasks change
    setInProgressTasks([]);
    setDelayedTasks([]);
    setCompletedTasks([]);
  }, [toDoTasks]);

  return (
    <div className="dashboard">
      <aside className="sidebar">
        <h2>Upcoming Events</h2>
        <EventCard />
      </aside>

      <main className="main-content">
        <h1>Main Content</h1>
        <p>This is the main content area of the dashboard.</p>
      </main>

      <div className="column-container">
        <div className="column">
          <div className="column-section" onDrop={handleToDoDrop} onDragOver={handleDragOver} onDragEnter={handleDragEnter} onDragLeave={handleDragLeave}>
            <h2>ToDo</h2>
            <TaskList tasks={toDoTasks} />
          </div>
        </div>

        <div className="column">
          <div className="column-section" onDrop={handleInProgressDrop} onDragOver={handleDragOver} onDragEnter={handleDragEnter} onDragLeave={handleDragLeave}>
            <h2>InProgress</h2>
            <InProgressTaskList tasks={inProgressTasks} />
          </div>
        </div>

        <div className="column">
          <div className="column-section" onDrop={handleCompletedDrop} onDragOver={handleDragOver} onDragEnter={handleDragEnter} onDragLeave={handleDragLeave}>
            <h2>Completed</h2>
            <CompletedTaskList tasks={completedTasks} />
          </div>
        </div>

        <div className="column">
          <div className="column-section" onDrop={handleDelayedDrop} onDragOver={handleDragOver} onDragEnter={handleDragEnter} onDragLeave={handleDragLeave}>
            <h2>Delayed</h2>
            <DelayedTaskList tasks={delayedTasks} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
