import React from 'react';
import { useDrag, useDrop } from 'react-dnd';
import Card from 'react-bootstrap/Card';
import { ItemTypes } from './ItemTypes';

function Task({ task, index, onDrop}) {
    const [{ isDragging }, drag] = useDrag({
        type: ItemTypes.TASK,
        item: { type: ItemTypes.TASK, task, index },
        collect: monitor => ({
          isDragging: monitor.isDragging(),
        }),
      });

      const [, drop] = useDrop({
        accept: ItemTypes.TASK,
        hover(item, monitor) {
          console.log('Hovering over task:', task); // Log the task being hovered over

          const dragIndex = item.index;
          const hoverIndex = index;

          // Don't replace items with themselves
          if (dragIndex === hoverIndex) {
            return;
          }

          // Determine rectangle on screen
          const hoverBoundingRect = ref.current.getBoundingClientRect();

          // Get vertical middle
          const hoverMiddleY = (hoverBoundingRect.bottom - hoverBoundingRect.top) / 2;

          // Determine mouse position
          const clientOffset = monitor.getClientOffset();

          // Get pixels to the top
          const hoverClientY = clientOffset.y - hoverBoundingRect.top;

          // Only perform the move when the mouse has crossed half of the items height
          // When dragging downwards, only move when the cursor is below 50%
          // When dragging upwards, only move when the cursor is above 50%

          // Dragging downwards
          if (dragIndex < hoverIndex && hoverClientY < hoverMiddleY) {
            return;
          }

          // Dragging upwards
          if (dragIndex > hoverIndex && hoverClientY > hoverMiddleY) {
            return;
          }

          // Perform the move
          onDrop(dragIndex, hoverIndex, item.task);
          item.index = hoverIndex;
        },
      });

      const ref = React.useRef(null);
      drag(ref);
      drop(ref);

  return (
    <div ref={ref} style={{ opacity: isDragging ? 0.5 : 1 }}>
      <Card>
        <Card.Body>
          <Card.Title>{task.name}</Card.Title>
          <Card.Text>
            <strong>Due Date:</strong> {task.due_date || 'No due date'}
            <br />
            <strong>Priority:</strong> {task.priority}
            <br />
            <strong>Assigned Users:</strong> {task.assigned_users.map(user => user.fullName).join(', ')}
          </Card.Text>
        </Card.Body>
      </Card>
    </div>
  );
}

export default Task;
