import React from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import '../index.css'; // Import your CSS file for styling

// Card component
const Card = ({ id, title, category, moveCard }) => {
    const [{ isDragging }, drag] = useDrag({
        type: 'CARD',
        item: { id, title, category },
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
    });

    return (
        <div ref={drag} className={`dashboard-card ${isDragging ? 'dragging' : ''}`}>
            {title}
        </div>
    );
};

class Dashboard extends React.Component {
    state = {
        upcomingEvents: [
            { id: 1, title: 'Meeting with Client', category: 'professional' },
            { id: 2, title: 'Birthday Party', category: 'social' },
            { id: 3, title: 'Project Deadline', category: 'professional' },
            { id: 4, title: 'Dinner with Friends', category: 'personal' },
            // Add more events as needed
        ],
        tasks: [
            { id: 1, title: 'Prepare presentation slides', category: 'professional' },
            { id: 2, title: 'Buy groceries', category: 'personal' },
            { id: 3, title: 'Call mom', category: 'personal' },
            // Add more tasks as needed
        ],
        selectedCategory: 'all'
    };

    handleCategoryChange = (category) => {
        this.setState({ selectedCategory: category });
    };

    moveCard = (id, category) => {
        // Update the category of the card with the given id
        const { upcomingEvents, tasks } = this.state;
        const updatedEvents = upcomingEvents.map(event =>
            event.id === id ? { ...event, category } : event
        );
        const updatedTasks = tasks.map(task =>
            task.id === id ? { ...task, category } : task
        );
        this.setState({ upcomingEvents: updatedEvents, tasks: updatedTasks });
    };

    render() {
        const { upcomingEvents, tasks, selectedCategory } = this.state;
        const filteredEvents = selectedCategory === 'all' ? upcomingEvents : upcomingEvents.filter(event => event.category === selectedCategory);

        return (
            <DndProvider backend={HTML5Backend}>
                <div className="dashboard-container">
                    <h2>Dashboard Overview</h2>
                    <div>
                        <h3>Upcoming Events</h3>
                        <div>
                            <select value={selectedCategory} onChange={(e) => this.handleCategoryChange(e.target.value)}>
                                <option value="all">All Categories</option>
                                <option value="personal">Personal</option>
                                <option value="professional">Professional</option>
                                <option value="social">Social</option>
                                {/* Add more categories as needed */}
                            </select>
                        </div>
                        <div className="dashboard-column">
                            {filteredEvents.map(event => (
                                <Card key={event.id} id={event.id} title={event.title} category={event.category} moveCard={this.moveCard} />
                            ))}
                        </div>
                    </div>
                    <div>
                        <h3>Tasks</h3>
                        <div className="dashboard-column">
                            {tasks.map(task => (
                                <Card key={task.id} id={task.id} title={task.title} category={task.category} moveCard={this.moveCard} />
                            ))}
                        </div>
                    </div>
                </div>
            </DndProvider>
        );
    }
}

export default Dashboard;
