import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';

function CreateEvent() {
  const [showModal, setShowModal] = useState(false);
  const [materials, setMaterials] = useState(['']); // Initial state with one empty field

  const handleCloseModal = () => setShowModal(false);
  const handleShowModal = () => setShowModal(true);

  const handleMaterialChange = (index, value) => {
    const updatedMaterials = [...materials];
    updatedMaterials[index] = value;
    setMaterials(updatedMaterials);
  };

  const handleAddMaterialField = () => {
    setMaterials([...materials, '']);
  };

  const handleRemoveMaterialField = (index) => {
    const updatedMaterials = [...materials];
    updatedMaterials.splice(index, 1);
    setMaterials(updatedMaterials);
  };

  useEffect(() => {
    handleShowModal(); // Show modal when component mounts
  }, []); // Empty dependency array ensures it runs only once after the first render

  return (
    <>


      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Create Event</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <fieldset >
            <Form.Group className="mb-3">
                <Form.Label htmlFor="disabledTextInput">Event Name</Form.Label>
                <Form.Control id="disabledTextInput" placeholder="Event Name" />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label htmlFor="disabledTextInput">Number of Participants</Form.Label>
                <Form.Control id="disabledTextInput" placeholder="Number of Participants" />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label htmlFor="disabledTextInput">Description</Form.Label>
                <Form.Control id="disabledTextInput" placeholder="Simple details about the Event" />
              </Form.Group>
              <Form.Group className="mb-3">
              <Form.Label>Materials Required</Form.Label>
              {materials.map((material, index) => (
                <div key={index} className="d-flex mb-2">
                  <Form.Control
                    type="text"
                    value={material.name}
                    onChange={(e) => handleMaterialChange(index, 'name', e.target.value)}
                    placeholder="Material"
                  />
                  <Form.Control
                    type="text"
                    value={material.cost}
                    onChange={(e) => handleMaterialChange(index, 'cost', e.target.value)}
                    placeholder="Cost"
                    className="ms-2"
                  />
                  <Button variant="danger" className="ms-2" onClick={() => handleRemoveMaterialField(index)}>
                    Remove
                  </Button>
                </div>
                ))}
                <Button variant="secondary" onClick={handleAddMaterialField}>Add Material</Button>
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label htmlFor="disabledTextInput">Estimated Cost</Form.Label>
                <Form.Control id="disabledTextInput" placeholder="Estimated Cost" />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label htmlFor="disabledTextInput">Venue</Form.Label>
                <Form.Control id="disabledTextInput" placeholder="Venue" />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label htmlFor="disabledTextInput">Time</Form.Label>
                <Form.Control id="disabledTextInput" placeholder="Time" />
              </Form.Group>
            </fieldset>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
          <Button variant="primary" onClick={handleCloseModal}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default CreateEvent;
