import React, { useContext } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import  TaskContext  from './TaskContext'; // Import TaskContext
import { useState } from 'react';

const Dashboard = () => {
  const { tasks, columns, setTasks, onDragEnd } = useContext(TaskContext); // Use TaskContext

  const reorderTasks = (list, startIndex, endIndex) => {
    const result = Array.from(list);
    const [removed] = result.splice(startIndex, 1);
    result.splice(endIndex, 0, removed);
    return result;
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="dashboard">
        <aside className="sidebar">
          <h2>Upcoming Events</h2>
          {/* Render EventCard or other components as needed */}
        </aside>
        <div className="main-section">
          <main className="main-content" style={{ height: "fit-content", width: "100%" }}>
            <h1>Main Content</h1>
            <p>This is the main content area of the dashboard.</p>
          </main>
          <div style={{ display: 'flex' }}>
            {Object.values(columns).map(column => (
              <Droppable droppableId={column.id} key={column.id}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    style={{
                      backgroundColor: 'lightgrey',
                      flex: '1',
                      margin: '8px',
                      minHeight: '200px'
                    }}
                  >
                    <div className="column">
                      <div className="column-section">
                        <h2>{column.title}</h2>
                        {tasks
                          .filter(task => task.column === column.id)
                          .map((task, index) => (
                            <Draggable key={task.id} draggableId={task.id} index={index}>
                              {(provided) => (
                                <div
                                  ref={provided.innerRef}
                                  {...provided.draggableProps}
                                  {...provided.dragHandleProps}
                                >
                                  <div>{task.content}</div>
                                </div>
                              )}
                            </Draggable>
                          ))}
                        {provided.placeholder}
                      </div>
                    </div>
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </div>
      </div>
    </DragDropContext>
  );
};

export default Dashboard;
