import React, { useState } from 'react';
import Task from './Task';

function DelayedTaskList({ onDrop }) {
  const [droppedTask, setDroppedTask] = useState(null); // State to hold the dropped task

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedTaskData = JSON.parse(e.dataTransfer.getData('task'));
    setDroppedTask(droppedTaskData); // Set the dropped task in state
    onDrop(droppedTaskData); // Trigger the onDrop function passed as prop
  };

  const allowDrop = (e) => {
    e.preventDefault();
  };

  return (
    <div onDrop={handleDrop} onDragOver={allowDrop}>
      {/* Render the dropped task if it exists */}
      {droppedTask && <Task key={droppedTask.id} index={0} task={droppedTask} />}
    </div>
  );
}

export default DelayedTaskList;
