import React, { useState, useEffect, useContext } from 'react';
import InProgressTaskList from './InProgressTaskList';
import DelayedTaskList from './DelayedTaskList';
import CompletedTaskList from './CompletedTaskList';
import EventCard from './EventCard';
import TaskList from './TaskList';
import TaskContext from './TaskContext'; // Import TaskContext

function Dashboard() {
  const [toDoTasks, setToDoTasks] = useState([]);
  const [inProgressTasks, setInProgressTasks] = useState([]);
  const [delayedTasks, setDelayedTasks] = useState([]);
  const [completedTasks, setCompletedTasks] = useState([]);
  const { sourceSection } = useContext(TaskContext); // Get sourceSection from TaskContext

  const handleToDoDrop = (index, task, event) => {
    if (event) {
      event.preventDefault();
      if (event.dataTransfer) {
        event.dataTransfer.dropEffect = 'move';
      }
    }

    console.log('Moving task to ToDo:', task);

    setToDoTasks(prevTasks => {
      const updatedTasks = [...prevTasks];
      const existingIndex = updatedTasks.findIndex(item => item.id === task.id);
      if (existingIndex !== -1) {
        updatedTasks.splice(existingIndex, 1);
      }
      updatedTasks.splice(index, 0, task);
      return updatedTasks;
    });
  };

  const handleInProgressDrop = (task) => {
    console.log('Moving task to InProgress:', task);

    setInProgressTasks(prevTasks => [...prevTasks, task]);
  };

  const handleCompletedDrop = (task) => {
    console.log('Moving task to Completed:', task);

    setCompletedTasks(prevTasks => [...prevTasks, task]);
  };

  const handleDelayedDrop = (task) => {
    console.log('Moving task to Delayed:', task);

    setDelayedTasks(prevTasks => [...prevTasks, task]);
  };  

  useEffect(() => {
    // Clear in progress, delayed, and completed tasks when toDoTasks change
    setInProgressTasks([]);
    setDelayedTasks([]);
    setCompletedTasks([]);
  }, [toDoTasks]);

  return (
    <div>
      <div className="dashboard">
        <aside className="sidebar">
          <h2>Upcoming Events</h2>
          <EventCard />
        </aside>
        <div className="main-section">
          <main className="main-content" style={{ height: "fit-content", width: "100%" }}>
            <h1>Main Content</h1>
            <p>This is the main content area of the dashboard.</p>
          </main>  
          <div className="column">
            <div className="column-section">
              <h2>ToDo</h2>
              <TaskList tasks={toDoTasks} onDrop={handleToDoDrop} sourceSection={sourceSection} />
            </div>
            </div>
            <div className="column">
            <div className="column-section">
              <h2>InProgress</h2>
              <InProgressTaskList tasks={inProgressTasks} onDrop={handleInProgressDrop} />
            </div>
            </div>
            <div className="column">
            <div className="column-section">
              <h2>Completed</h2>
              <CompletedTaskList tasks={completedTasks} onDrop={handleCompletedDrop} />
            </div>
            <div className="column-section">
              <h2>Delayed</h2>
              <DelayedTaskList tasks={delayedTasks} onDrop={handleDelayedDrop} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
