import React, { useContext } from 'react';
import { useDrag, useDrop } from 'react-dnd';
import Card from 'react-bootstrap/Card';
import TaskContext from './TaskContext';
import { ItemTypes } from './ItemTypes';
import { useAuth } from './AuthContext';

function Task({ task, index }) {
  const { tasks, updateTaskList } = useContext(TaskContext);
  const { user } = useAuth(); // Assuming AuthContext provides user info
console.log(user)
  const isAdmin = user && user.role === 'admin'; // Check if user is admin

  const [{ isDragging }, drag] = useDrag({
    type: ItemTypes.TASK,
    item: { type: ItemTypes.TASK, task, index },
    collect: monitor => ({
      isDragging: monitor.isDragging(),
    }),
  });

  const [, drop] = useDrop({
    accept: ItemTypes.TASK,
    hover(item, monitor) {
      const dragIndex = item.index;
      const hoverIndex = index;

      if (dragIndex === hoverIndex) {
        return;
      }

      const newTasks = [...tasks];
      const dragTask = newTasks[dragIndex];
      newTasks.splice(dragIndex, 1);
      newTasks.splice(hoverIndex, 0, dragTask);

      updateTaskList(newTasks);
    },
  });

  const handleDelete = async () => {
    try {
      const response = await fetch(`your_api_endpoint/tasks/${task.id}`, {
        method: 'DELETE',
      });
      if (response.ok) {
        console.log("Task deleted successfully:", task.id);
        // Optionally, you can update your state to reflect the deletion
      } else {
        console.error("Error deleting task:", response.statusText);
      }
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  const handleUpdate = async () => {
    try {
      const updatedTask = { ...task, /* update your task properties here */ };
      const response = await fetch(`your_api_endpoint/tasks/${task.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedTask),
      });
      if (response.ok) {
        console.log("Task updated successfully:", task.id);
        // Optionally, you can update your state to reflect the update
      } else {
        console.error("Error updating task:", response.statusText);
      }
    } catch (error) {
      console.error("Error updating task:", error);
    }
  };

  const ref = React.useRef(null);
  drag(ref);
  drop(ref);

  return (
    <div ref={ref} style={{ opacity: isDragging ? 0.5 : 1 }}>
      <Card>
        <Card.Body>
          <Card.Title>{task.name}</Card.Title>
          <Card.Text>
            <strong>Due Date:</strong> {task.due_date || 'No due date'}
            <br />
            <strong>Priority:</strong> {task.priority}
            <br />
            <strong>Assigned Users:</strong> {task.assigned_users.map(user => user.fullName).join(', ')}
          </Card.Text>
          {isAdmin && (
            <>
              <button onClick={handleDelete}>Delete</button>
              <button onClick={handleUpdate}>Update</button>
            </>
          )}
        </Card.Body>
      </Card>
    </div>
  );
}

export default Task;
