import React from 'react';
import '../index.css'; // Import your CSS file for styling

class Dashboard extends React.Component {
    state = {
        upcomingEvents: [
            { id: 1, title: 'Meeting with Client', category: 'professional' },
            { id: 2, title: 'Birthday Party', category: 'social' },
            { id: 3, title: 'Project Deadline', category: 'professional' },
            { id: 4, title: 'Dinner with Friends', category: 'personal' },
            // Add more events as needed
        ],
        tasks: [
            { id: 1, title: 'Prepare presentation slides', category: 'professional' },
            { id: 2, title: 'Buy groceries', category: 'personal' },
            { id: 3, title: 'Call mom', category: 'personal' },
            // Add more tasks as needed
        ],
        selectedCategory: 'all'
    };

    handleCategoryChange = (category) => {
        this.setState({ selectedCategory: category });
    };

    render() {
        const { upcomingEvents, tasks, selectedCategory } = this.state;
        const filteredEvents = selectedCategory === 'all' ? upcomingEvents : upcomingEvents.filter(event => event.category === selectedCategory);

        return (
            <div className="dashboard-container">
                <h2>Dashboard Overview</h2>
                <div>
                    <h3>Upcoming Events</h3>
                    <div>
                        <select value={selectedCategory} onChange={(e) => this.handleCategoryChange(e.target.value)}>
                            <option value="all">All Categories</option>
                            <option value="personal">Personal</option>
                            <option value="professional">Professional</option>
                            <option value="social">Social</option>
                            {/* Add more categories as needed */}
                        </select>
                    </div>
                    <div className="dashboard-column" onDrop={this.drop} onDragOver={this.allowDrop}>
                        {filteredEvents.map(event => (
                            <div key={event.id} className="dashboard-card" draggable="true" onDragStart={this.drag}>
                                {event.title}
                            </div>
                        ))}
                    </div>
                </div>
                <div>
                    <h3>Tasks</h3>
                    <div className="dashboard-column" onDrop={this.drop} onDragOver={this.allowDrop}>
                        {tasks.map(task => (
                            <div key={task.id} className="dashboard-card" draggable="true" onDragStart={this.drag}>
                                {task.title}
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        );
    }

    allowDrop = (event) => {
        event.preventDefault();
    };

    drag = (event) => {
        event.dataTransfer.setData("text", event.target.id);
    };

    drop = (event) => {
        event.preventDefault();
        const data = event.dataTransfer.getData("text");
        event.target.appendChild(document.getElementById(data)); // Appends the card to the drop target
    };
}

export default Dashboard;
