// In TaskList.js
import React, { useContext } from 'react';
import Task from './Task';
import TaskContext from './TaskContext';
import { useAuth } from './AuthContext';

function TaskList() {
  const { tasks, setTasks, sourceSection, setSourceSection, onDrop } = useContext(TaskContext);
  const { user } = useAuth();

  const userTasks = tasks.filter(task => task.assigned_users.some(assignedUser => assignedUser.id === user.id));



  return (
    <div >
      {userTasks.map((task, index) => (
       <Task
          key={task.id}
          index={index}
          task={task}
          moveTask={(dragIndex, hoverIndex) => moveTask(dragIndex, hoverIndex, task.section, 'targetSection')} 
          sourceSection={task.section} // Ensure sourceSection is correctly passed
          isAdmin={user.role === 'admin'}
        />

      ))}
    </div>
  );
}

export default TaskList;
