// TaskList.js
import React, { useContext, useEffect, useState } from 'react';
import Task from './Task';
import TaskContext from './TaskContext';
import { useAuth } from './AuthContext';
import { useDrop } from 'react-dnd';

function TaskList() {
  const { tasks, setTasks } = useContext(TaskContext);
  const { user } = useAuth();
  const [draggedTaskId, setDraggedTaskId] = useState(null);

  useEffect(() => {
    // Fetch tasks from the database or local storage
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      // Fetch tasks from your API or from local storage
      // Update the tasks state in TaskContext
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleDrop = (draggedId, targetIndex) => {
    const updatedTasks = [...tasks];
    const draggedTask = updatedTasks.find(task => task.id === draggedId);
    const draggedIndex = updatedTasks.indexOf(draggedTask);
    const direction = draggedIndex < targetIndex ? 1 : -1;

    // Update positions locally
    updatedTasks.splice(draggedIndex, 1);
    updatedTasks.splice(targetIndex + (direction === 1 ? -1 : 0), 0, draggedTask);
    setTasks(updatedTasks);

    // Update positions in local storage
    try {
      localStorage.setItem('tasks', JSON.stringify(updatedTasks));
    } catch (error) {
      console.error('Error updating task positions in local storage:', error);
    }
  };


  const [{ isOver }, drop] = useDrop({
    accept: 'TASK',
    drop: (item, monitor) => {
      const draggedId = item.id;
      const targetIndex = tasks.length; // Dropping at the end of the list
      handleDrop(draggedId, targetIndex);
    },
    collect: monitor => ({
      isOver: !!monitor.isOver(),
    }),
  });

  return (
    <div ref={drop} style={{ minHeight: '100vh', background: isOver ? 'lightblue' : 'white' }}>
      {tasks.map((task, index) => (
        <Task
          key={task.id}
          index={index}
          task={task}
          sourceSection={task.section}
          isAdmin={user.role === 'admin'}
          setDraggedTaskId={setDraggedTaskId}
          handleDrop={handleDrop}
          draggedTaskId={draggedTaskId}
        />
      ))}
    </div>
  );
}

export default TaskList;
