import React, { useContext, useEffect } from 'react';
import Task from './Task';
import TaskContext from './TaskContext';
import { useAuth } from './AuthContext';
import { useDrop } from 'react-dnd';

function TaskList() {
  const { tasks, setTasks } = useContext(TaskContext);
  const { user } = useAuth();

  useEffect(() => {
    // Fetch tasks from the database or local storage
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      // Retrieve tasks from local storage
      const storedTasks = localStorage.getItem('tasks');

      // If tasks are found in local storage, parse and update the tasks state in TaskContext
      if (storedTasks) {
        const parsedTasks = JSON.parse(storedTasks);

        // Sort tasks based on their positions
        parsedTasks.sort((a, b) => a.position - b.position);

        setTasks(parsedTasks);
      }
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleDrop = (draggedId, targetIndex) => {
    const updatedTasks = [...tasks];
    const draggedTask = updatedTasks.find(task => task.id === draggedId);
    const draggedIndex = updatedTasks.indexOf(draggedTask);

    // Calculate the target index based on the horizontal position
    const targetX = targetIndex * TASK_WIDTH; // Assuming TASK_WIDTH is the width of each task
    const draggedX = draggedIndex * TASK_WIDTH;
    const direction = draggedX < targetX ? 1 : -1;
    const targetIndexHorizontal = Math.floor(targetX / TASK_WIDTH);

    // Update positions locally
    updatedTasks.splice(draggedIndex, 1);
    updatedTasks.splice(targetIndexHorizontal + (direction === 1 ? -1 : 0), 0, draggedTask);
    setTasks(updatedTasks);

    // Update positions in local storage
    try {
      localStorage.setItem('tasks', JSON.stringify(updatedTasks));
    } catch (error) {
      console.error('Error updating task positions in local storage:', error);
    }
  };


  const [{ isOver }, drop] = useDrop({
    accept: 'TASK',
    drop: (item, monitor) => {
      const draggedId = item.id;
      const targetIndex = tasks.length; // Dropping at the end of the list
      handleDrop(draggedId, targetIndex);
    },
    collect: monitor => ({
      isOver: !!monitor.isOver(),
    }),
  });

  return (
    <div ref={drop} style={{ display: 'flex', minHeight: '100vh', background: isOver ? 'lightblue' : 'white' }}>
      {tasks.map((task, index) => (
        <Task
          key={task.id}
          index={index}
          task={task}
          sourceSection={task.section}
          isAdmin={user.role === 'admin'}
        />
      ))}
    </div>
  );
}

export default TaskList;
