import React, { useState } from 'react';
import '../index.css'; // Assuming you have a separate CSS file for styles
import EventsList from './EventCard';
import TaskList from './TaskList';

function Dashboard() {
  const [toDoTasks, setToDoTasks] = useState([]);
  const [inProgressTasks, setInProgressTasks] = useState([]);
  const [delayedTasks, setDelayedTasks] = useState([]);

  // Function to handle drop event for "ToDo" section
  const handleToDoDrop = task => {
    // Check if the task is in progress or delayed
    if (task.status === 'inProgress') {
      setInProgressTasks(prevTasks => [...prevTasks, task]);
    } else if (task.status === 'delayed') {
      setDelayedTasks(prevTasks => [...prevTasks, task]);
    }
    // Remove the task from the ToDo column
    setToDoTasks(prevTasks => prevTasks.filter(t => t.id !== task.id));
  };

  // Function to handle drop event for "InProgress" section
  const handleInProgressDrop = task => {
    setInProgressTasks(prevTasks => {
      const updatedTasks = prevTasks.filter(t => t.id !== task.id);
      return [...updatedTasks, task];
    });
  };

  // Function to handle drop event for "Delayed" section
  const handleDelayedDrop = task => {
    setDelayedTasks(prevTasks => {
      const updatedTasks = prevTasks.filter(t => t.id !== task.id);
      return [...updatedTasks, task];
    });
  };

  return (
    <div className="dashboard">
      <aside className="sidebar">
        <h2>Upcoming Events</h2>
        <ul>
          <EventsList />
        </ul>
      </aside>
      <div className="separator"></div> 
      <div className="column">
        <div className="column-section">
          <h2>ToDo</h2>
          <ul>
            <TaskList tasks={toDoTasks} onDrop={handleToDoDrop} />
          </ul>
        </div>

        <div className="column-section">
          <h2>InProgress</h2>
          <ul>
            <TaskList tasks={inProgressTasks} onDrop={handleInProgressDrop} />
          </ul>
        </div>

        <div className="column-section">
          <h2>Delayed</h2>
          <ul>
            <TaskList tasks={delayedTasks} onDrop={handleDelayedDrop} />
          </ul>
        </div>
      </div>
      <main className="main-content" style={{ marginLeft: -1202 }}>
        <h1>Main Content</h1>
        <p>This is the main content area of the dashboard.</p>
      </main>
    </div>
  );
}

export default Dashboard;
