import React, { useState } from 'react';
import Task from './Task';

const Dashboard = () => {
  const [toDoTasks, setToDoTasks] = useState([]);
  const [inProgressTasks, setInProgressTasks] = useState([]);
  const [completedTasks, setCompletedTasks] = useState([]);
  const [delayedTasks, setDelayedTasks] = useState([]);

  const handleToDoDrop = (task) => {
    // Add the dropped task to the ToDo section
    setToDoTasks(prevTasks => [...prevTasks, task]);
  };

  const handleInProgressDrop = (task) => {
    // Add the dropped task to the InProgress section
    setInProgressTasks(prevTasks => [...prevTasks, task]);
  };

  const handleCompletedDrop = (task) => {
    // Add the dropped task to the Completed section
    setCompletedTasks(prevTasks => [...prevTasks, task]);
  };

  const handleDelayedDrop = (task) => {
    // Add the dropped task to the Delayed section
    setDelayedTasks(prevTasks => [...prevTasks, task]);
  };

  return (
    <div className="dashboard">
      <div className="column-section">
        <h2>ToDo</h2>
        <div onDrop={() => handleToDoDrop()} onDragOver={(e) => e.preventDefault()}>
          {toDoTasks.map(task => <Task key={task.id} task={task} />)}
        </div>
      </div>
      <div className="column-section">
        <h2>InProgress</h2>
        <div onDrop={() => handleInProgressDrop()} onDragOver={(e) => e.preventDefault()}>
          {inProgressTasks.map(task => <Task key={task.id} task={task} />)}
        </div>
      </div>
      <div className="column-section">
        <h2>Completed</h2>
        <div onDrop={() => handleCompletedDrop()} onDragOver={(e) => e.preventDefault()}>
          {completedTasks.map(task => <Task key={task.id} task={task} />)}
        </div>
      </div>
      <div className="column-section">
        <h2>Delayed</h2>
        <div onDrop={() => handleDelayedDrop()} onDragOver={(e) => e.preventDefault()}>
          {delayedTasks.map(task => <Task key={task.id} task={task} />)}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
