// In TaskList.js
import React, { useContext } from 'react';
import Task from './Task';
import TaskContext from './TaskContext';
import { useAuth } from './AuthContext';
import { useDrop } from 'react-dnd';

function TaskList() {
  const { tasks } = useContext(TaskContext);
  const { user } = useAuth();

  const userTasks = tasks.filter(task => task.assigned_users.some(assignedUser => assignedUser.id === user.id));
  const [{ isOver }, drop] = useDrop({
    accept: 'TASK',
    drop: (item, monitor) => {
      // Handle drop logic here
    },
    collect: monitor => ({
      isOver: !!monitor.isOver(),
    }),
  });



  return (
    <div ref={drop} style={{ background: isOver ? 'lightblue' : 'white' }}>
      {userTasks.map((task, index) => (
       <Task
          key={task.id}
          index={index}
          task={task}
          sourceSection={task.section} // Ensure sourceSection is correctly passed
          isAdmin={user.role === 'admin'}
        />

      ))}
    </div>
  );
}

export default TaskList;
