// Dashboard.js
import React, { useState } from 'react';
import TaskList from './TaskList';
import EventCard from './EventCard';

function Dashboard() {
  const [toDoTasks, setToDoTasks] = useState([]);
  const [inProgressTasks, setInProgressTasks] = useState([]);
  const [delayedTasks, setDelayedTasks] = useState([]);

useEffect(() => {
  setInProgressTasks([]);
  setDelayedTasks([]);
}, []);

  const handleToDoDrop = (index, task) => {
    if (task.status === 'inProgress') {
      setInProgressTasks(prevTasks => [...prevTasks, task]);
    } else if (task.status === 'delayed') {
      setDelayedTasks(prevTasks => [...prevTasks, task]);
    }

    setToDoTasks(prevTasks => {
      const updatedTasks = [...prevTasks];
      // If the dropped task is already in ToDo tasks, remove it first
      const existingIndex = updatedTasks.findIndex(item => item.id === task.id);
      if (existingIndex !== -1) {
        updatedTasks.splice(existingIndex, 1);
      }
      // Then, insert the task at the new position
      updatedTasks.splice(index, 0, task);
      return updatedTasks;
    });
  };

  const handleInProgressDrop = (index, task) => {
    if (task.status === 'toDo') {
      // If the dropped task is from ToDo, remove it from the ToDo list
      setToDoTasks(prevTasks => prevTasks.filter((_, i) => i !== index));
    } else if (task.status === 'delayed') {
      // If the dropped task is from delayed, remove it from the delayed list
      setDelayedTasks(prevTasks => prevTasks.filter((_, i) => i !== index));
    }

    // Add the dropped task to the inProgress list
    setInProgressTasks(prevTasks => [...prevTasks, task]);
  };

  const handleDelayedDrop = (index, task) => {
    if (task.status === 'toDo') {
      // If the dropped task is from ToDo, remove it from the ToDo list
      setToDoTasks(prevTasks => prevTasks.filter((_, i) => i !== index));
    } else if (task.status === 'inProgress') {
      // If the dropped task is from inProgress, remove it from the inProgress list
      setInProgressTasks(prevTasks => prevTasks.filter((_, i) => i !== index));
    }

    // Add the dropped task to the delayed list
    setDelayedTasks(prevTasks => [...prevTasks, task]);
  };


  return (
    <div className="dashboard">
      <aside className="sidebar">
        <h2>Upcoming Events</h2>
        <EventCard />
      </aside>
      <div className="separator"></div>
      <div className="column">
        <div className="column-section">
          <h2>ToDo</h2>
          <TaskList tasks={toDoTasks} onDrop={handleToDoDrop} />
        </div>

        <div className="column-section">
          <h2>InProgress</h2>
          <TaskList tasks={inProgressTasks} onDrop={handleInProgressDrop} />
        </div>

        <div className="column-section">
          <h2>Delayed</h2>
          <TaskList tasks={delayedTasks} onDrop={handleDelayedDrop} />
        </div>
      </div>
      <main className="main-content">
        <h1>Main Content</h1>
        <p>This is the main content area of the dashboard.</p>
      </main>
    </div>
  );
}

export default Dashboard;
