import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import EventCard from './EventCard';
import TaskList from './TaskList';
import { v4 as uuidv4 } from 'uuid'; // Import uuid

const Dashboard = () => {
  const [columns, setColumns] = useState({
    todo: {
      id: 'todo', // Use fixed IDs for Droppable components
      title: 'ToDo',
      items: [{ id: 'task1', title: 'Task 1' }]
    },
    inProgress: {
      id: 'inProgress',
      title: 'InProgress',
      items: []
    },
    completed: {
      id: 'completed',
      title: 'Completed',
      items: []
    },
    delayed: {
      id: 'delayed',
      title: 'Delayed',
      items: []
    }
  });

  useEffect(() => {
    // Generate unique IDs using uuid only once during component initialization
    const newColumns = {};
    Object.keys(columns).forEach(key => {
      newColumns[key] = { ...columns[key], id: uuidv4() };
    });
    setColumns(newColumns);
  }, []); // Empty dependency array ensures this effect runs only once

  const onDragEnd = result => {
    const { source, destination, draggableId } = result;
    if (!destination) return;

    const sourceColumn = columns[source.droppableId];
    const destinationColumn = columns[destination.droppableId];
    const newSourceItems = Array.from(sourceColumn.items);
    const newDestinationItems = Array.from(destinationColumn.items);

    if (source.droppableId === destination.droppableId) {
      // Reorder within the same column
      newSourceItems.splice(source.index, 1);
      newSourceItems.splice(destination.index, 0, sourceColumn.items.find(item => item.id === draggableId));
      const newColumns = {
        ...columns,
        [source.droppableId]: {
          ...sourceColumn,
          items: newSourceItems,
        },
      };
      setColumns(newColumns);
    } else {
      // Move from one column to another
      newSourceItems.splice(source.index, 1);
      newDestinationItems.splice(destination.index, 0, sourceColumn.items.find(item => item.id === draggableId));
      const newColumns = {
        ...columns,
        [source.droppableId]: {
          ...sourceColumn,
          items: newSourceItems,
        },
        [destination.droppableId]: {
          ...destinationColumn,
          items: newDestinationItems,
        },
      };
      setColumns(newColumns);
    }
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="dashboard">
        <aside className="sidebar">
          <h2>Upcoming Events</h2>
          <EventCard />
        </aside>
        <div className="main-section">
          <main className="main-content" style={{ height: "fit-content", width: "100%" }}>
            <h1>Main Content</h1>
            <p>This is the main content area of the dashboard.</p>
          </main>
          <div style={{ display: 'flex' }}>
            {Object.values(columns).map(column => (
              <Droppable droppableId={column.id} key={column.id}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    style={{
                      backgroundColor: 'lightgrey',
                      flex: '1',
                      margin: '8px',
                      minHeight: '200px'
                    }}
                  >
                    <div className="column">
                      <div className="column-section">
                        <h2>{column.title}</h2>
                        {/* Render tasks without updating state here */}
                        {column.items.map((item, index) => (
                          <Draggable key={item.id} draggableId={item.id} index={index}>
                            {(provided) => (
                              <div
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                              >
                                <TaskList task={item} droppableId={column.id} />
                              </div>
                            )}
                          </Draggable>
                        ))}
                        {provided.placeholder}
                      </div>
                    </div>
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </div>
      </div>
    </DragDropContext>
  );
};

export default Dashboard;
