import React, { useState, useEffect } from 'react';
import { useDrag, useDrop } from 'react-dnd';
import Card from 'react-bootstrap/Card';
import { ItemTypes } from './ItemTypes'; // Assuming ItemTypes is defined in a separate file

function Task({ task, index, moveTask }) {
  const [{ isDragging }, drag] = useDrag({
    type: ItemTypes.TASK,
    item: { type: ItemTypes.TASK, task, index },
    collect: monitor => ({
      isDragging: !!monitor.isDragging(),
    }),
  });

  const [, drop] = useDrop({
    accept: ItemTypes.TASK,
    hover() {},
    drop: (item) => {
      moveTask(item.index, index);
    },
  });

  const ref = React.useRef(null);
  drag(drop(ref));

  return (
    <div
      ref={ref}
      style={{ opacity: isDragging ? 0.5 : 1 }}
      role="listitem"
    >
      <Card>
        <Card.Body>
          <Card.Title>{task.name}</Card.Title>
          <Card.Text>
            <strong>Description:</strong> {task.description || 'No description'}
            <br />
            <strong>Due Date:</strong> {task.due_date || 'No due date'}
            <br />
            <strong>Priority:</strong> {task.priority}
            <br />
            <strong>Assigned Users:</strong> {task.assigned_users.map(user => user.fullName).join(', ')}
          </Card.Text>
        </Card.Body>
      </Card>
    </div>
  );
}

function TaskList() {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    fetchTasks();
  }, []); // Empty dependency array to fetch tasks only once on component mount

  const fetchTasks = async () => {
    try {
      const response = await fetch('http://localhost:5555/api/tasks');
      if (!response.ok) {
        throw new Error('Failed to fetch tasks');
      }
      const data = await response.json();
      setTasks(data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const moveTask = (dragIndex, hoverIndex) => {
    const draggedTask = tasks[dragIndex];
    const updatedTasks = [...tasks];
    updatedTasks.splice(dragIndex, 1);
    updatedTasks.splice(hoverIndex, 0, draggedTask);
    setTasks(updatedTasks);
  };

  return (
    <div>
      {tasks.map((task, index) => (
        <Task key={task.id} index={index} task={task} moveTask={moveTask} />
      ))}
    </div>
  );
}

export default TaskList;
