import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';

function CreateTask() {
  const [showModal, setShowModal] = useState(false);
  const [materials, setMaterials] = useState(['']); // Initial state with one empty field

  const handleCloseModal = () => setShowModal(false);
  const handleShowModal = () => setShowModal(true);

  const handleMaterialChange = (index, value) => {
    const updatedMaterials = [...materials];
    updatedMaterials[index] = value;
    setMaterials(updatedMaterials);
  };

  const handleAddMaterialField = () => {
    setMaterials([...materials, '']);
  };

  const handleRemoveMaterialField = (index) => {
    const updatedMaterials = [...materials];
    updatedMaterials.splice(index, 1);
    setMaterials(updatedMaterials);
  };

  return (
    <>
      <Button variant="primary" onClick={handleShowModal}>
        Open Modal
      </Button>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Create Event</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <fieldset >
            <Form.Group className="mb-3">
                <Form.Label htmlFor="disabledTextInput">Task Name</Form.Label>
                <Form.Control id="disabledTextInput" placeholder="Event Name" />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label htmlFor="disabledTextInput">Date Due</Form.Label>
                <Form.Control id="disabledTextInput" placeholder="Number of Participants" />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label htmlFor="disabledTextInput">Priority</Form.Label>
                <Form.Control id="disabledTextInput" placeholder="Number of Participants" />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label htmlFor="disabledTextInput">Participants</Form.Label>
                <Form.Control id="disabledTextInput" placeholder="Number of Participants" />
              </Form.Group>

            </fieldset>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
          <Button variant="primary" onClick={handleCloseModal}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default CreateTask;
