import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import '../index.css'; // Assuming you have a separate CSS file for styles
import EventsList from './EventCard';
import TaskList from './TaskList';

function Dashboard() {
  const [tasks, setTasks] = useState([]);

  const handleDragEnd = (result) => {
    if (!result.destination) return; // Dropped outside the droppable area

    const updatedTasks = [...tasks];
    const [movedTask] = updatedTasks.splice(result.source.index, 1);
    movedTask.column = result.destination.droppableId;
    updatedTasks.splice(result.destination.index, 0, movedTask);

    setTasks(updatedTasks);
  };

  return (
    <div className="dashboard">
      <aside className="sidebar">
        <h2>Upcoming Events</h2>
        <EventsList />
      </aside>
      <div className="separator"></div> 
      <div className="column">
        <DragDropContext onDragEnd={handleDragEnd}>
          <Droppable droppableId="columns" direction="horizontal">
            {(provided) => (
              <div className="column-container" {...provided.droppableProps} ref={provided.innerRef}>
                <div className="column-section">
                  <h2>ToDo</h2>
                  <TaskList tasks={tasks.filter(task => task.column === 'ToDo')} />
                </div>
                <div className="column-section">
                  <h2>InProgress</h2>
                  <TaskList tasks={tasks.filter(task => task.column === 'InProgress')} />
                </div>
                <div className="column-section">
                  <h2>Delayed</h2>
                  <TaskList tasks={tasks.filter(task => task.column === 'Delayed')} />
                </div>
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>
      </div>
      <main className="main-content" style={{ marginLeft: -1202 }}>
        <h1>Main Content</h1>
        <p>This is the main content area of the dashboard.</p>
      </main>
    </div>
  );
}

export default Dashboard;
