import React, { useState, useContext, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import EventCard from './EventCard';
import TaskList from './TaskList';
import { v4 as uuidv4 } from 'uuid'; // Import uuid
import TaskContext  from './TaskContext'; // Import TaskContext

const Dashboard = () => {
  const { tasks, setTasks, columnIds, setColumnIds } = useContext(TaskContext); // Use TaskContext

  const [columns, setColumns] = useState({
    todo: {
      id: uuidv4(),
      title: 'ToDo',
      items: [
        { id: uuidv4(), title: 'Task 1' }
      ]
    },
    inProgress: {
      id: uuidv4(),
      title: 'InProgress',
      items: []
    },
    completed: {
      id: uuidv4(),
      title: 'Completed',
      items: []
    },
    delayed: {
      id: uuidv4(),
      title: 'Delayed',
      items: []
    }
  });
  useEffect(() => {
    // Generate unique IDs for each column based on the tasks
    const updatedColumns = Object.keys(columns).reduce((acc, key) => {
      return {
        ...acc,
        [key]: {
          ...columns[key],
          id: uuidv4() // Generate a new UUID for each column
        }
      };
    }, {});

    // Update the columns state with new IDs
    setColumns(updatedColumns);
  }, [tasks]); // Update whenever tasks change


  const onDragEnd = result => {
    const { source, destination, draggableId } = result;
    if (!destination) return;

    const sourceColumn = columns[source.droppableId];
    const destinationColumn = columns[destination.droppableId];

    console.log('Source droppable ID:', source.droppableId);
    console.log('Destination droppable ID:', destination.droppableId);

    if (!sourceColumn || !destinationColumn) {
      console.error('Invalid source or destination column');
      return;
    }

    // Get the task being dragged
    const draggedTask = tasks.find(task => task.id === draggableId);

    if (!draggedTask) {
      console.error('Invalid dragged task');
      return;
    }

    // Remove the dragged task from the source column
    const updatedSourceItems = sourceColumn.items.filter(item => item.id !== draggableId);

    // Add the dragged task to the destination column
    const updatedDestinationItems = [...destinationColumn.items];
    updatedDestinationItems.splice(destination.index, 0, draggedTask);

    // Update the tasks state
    const updatedTasks = tasks.map(task => {
      if (task.id === draggableId) {
        return { ...task, column: destinationColumn.title }; // Update the column property of the task
      }
      return task;
    });

    // Update the columns state
    const updatedColumns = {
      ...columns,
      [sourceColumn.title.toLowerCase()]: { ...sourceColumn, items: updatedSourceItems },
      [destinationColumn.title.toLowerCase()]: { ...destinationColumn, items: updatedDestinationItems }
    };

    setTasks(updatedTasks);
    setColumns(updatedColumns);
  };


  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="dashboard">
        <aside className="sidebar">
          <h2>Upcoming Events</h2>
          <EventCard />
        </aside>
        <div className="main-section">
          <main className="main-content" style={{ height: "fit-content", width: "100%" }}>
            <h1>Main Content</h1>
            <p>This is the main content area of the dashboard.</p>
          </main>
          <div style={{ display: 'flex' }}>
            {Object.values(columns).map(column => (
              <Droppable droppableId={column.id} key={column.id}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    style={{
                      backgroundColor: 'lightgrey',
                      flex: '1',
                      margin: '8px',
                      minHeight: '200px'
                    }}
                  >
                    <div className="column">
                      <div className="column-section">
                        <h2>{column.title}</h2>
                        {column.items.map((item, index) => (
                              <Draggable key={String(item.id)} draggableId={String(item.id)} index={index}>
                                {(provided) => (
                                  <div
                                    ref={provided.innerRef}
                                    {...provided.draggableProps}
                                    {...provided.dragHandleProps}
                                  >
                                    <TaskList task={item} droppableId={column.id} />
                                  </div>
                                )}
                              </Draggable>
                            ))}
                          {provided.placeholder}
                      </div>
                    </div>
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </div>
      </div>
    </DragDropContext>
  );
};

export default Dashboard;
