import React, { useContext } from 'react';
import Task from './Task';
import TaskContext from './TaskContext';
import { useAuth } from './AuthContext';

function TaskList({ onDrop }) {
  const { tasks, setTasks, sourceSection } = useContext(TaskContext);
  const { user } = useAuth();

  const handleDrop = (e) => {
    e.preventDefault();

    try {
      const droppedTaskData = e.dataTransfer.getData('task');
      if (!droppedTaskData) {
        // Handle empty data
        console.error("Error: Empty dropped task data.");
        return;
      }

      const droppedTask = JSON.parse(droppedTaskData);
      // Proceed with handling the dropped task
      onDrop(droppedTask);
    } catch (error) {
      console.error("Error parsing dropped task data:", error);
    }
  };

  const allowDrop = (e) => {
    e.preventDefault();
  };

  const moveTask = (dragIndex, hoverIndex, sourceSection, targetSection) => {
    if (!tasks || !tasks[sourceSection]) {
      console.error("Error: Source section is not defined or does not exist.");
      return;
    }

    // Create a copy of the tasks array for the source section
    const updatedSourceTasks = [...tasks[sourceSection]];

    // Get the dragged task
    const draggedTask = updatedSourceTasks[dragIndex];

    // Remove the task from the source section
    updatedSourceTasks.splice(dragIndex, 1);

    // Update the tasks array in state
    setTasks({
      ...tasks,
      [sourceSection]: updatedSourceTasks,
    });
  };

  return (
    <div onDrop={handleDrop} onDragOver={allowDrop}>
      {tasks && tasks[sourceSection] && tasks[sourceSection].map((task, index) => (
        <Task
          key={task.id}
          index={index}
          task={task}
          moveTask={(dragIndex, hoverIndex) => moveTask(dragIndex, hoverIndex, sourceSection, task.section)} // Pass target section dynamically
          sourceSection={sourceSection}
          isAdmin={user && user.role === 'admin'} // Check if user exists before accessing role property
        />
      ))}
    </div>
  );
}

export default TaskList;
