import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import EventCard from './EventCard';
import TaskList from './TaskList';
import { v4 as uuidv4 } from 'uuid'; // Import uuid

const Dashboard = () => {
  const [columns, setColumns] = useState({
    todo: {
      id: 'todo', // Assign unique IDs to each column
      title: 'ToDo',
      items: [
        { id: 'task1', title: 'Task 1' }
      ]
    },
    inProgress: {
      id: 'inProgress', // Static key for InProgress column
      title: 'InProgress',
      items: []
    },
    completed: {
      id: 'completed', // Static key for Completed column
      title: 'Completed',
      items: []
    },
    delayed: {
      id: 'delayed', // Static key for Delayed column
      title: 'Delayed',
      items: []
    }
  });

  const [renderedTasks, setRenderedTasks] = useState({});

  const onDragEnd = result => {
    // Drag and drop logic
  };

  const renderTask = (taskId) => {
    if (!renderedTasks[taskId]) {
      setRenderedTasks({
        ...renderedTasks,
        [taskId]: true
      });
      return true;
    }
    return false;
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="dashboard">
        <aside className="sidebar">
          <h2>Upcoming Events</h2>
          <EventCard />
        </aside>
        <div className="main-section">
          <main className="main-content" style={{ height: "fit-content", width: "100%" }}>
            <h1>Main Content</h1>
            <p>This is the main content area of the dashboard.</p>
          </main>
          <div style={{ display: 'flex' }}>
            <Droppable droppableId={columns.todo.id} key={columns.todo.id}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  style={{
                    backgroundColor: 'lightgrey',
                    flex: '1',
                    margin: '8px',
                    minHeight: '200px'
                  }}
                >
                  <div className="column">
                    <div className="column-section">
                      <h2>{columns.todo.title}</h2>
                      {/* Render the task without updating state here */}
                      {columns.todo.items.map((item, index) => (
                        <Draggable key={item.id} draggableId={item.id} index={index}>
                          {(provided) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                            >
                              <TaskList task={item} droppableId={columns.todo.id} />
                            </div>
                          )}
                        </Draggable>
                      ))}
                      {provided.placeholder}
                    </div>
                  </div>
                </div>
              )}
            </Droppable>
          </div>
        </div>
      </div>
    </DragDropContext>
  );
};

export default Dashboard;
