import React, { useContext } from 'react';
import Task from './Task';
import TaskContext from './TaskContext';
import { useAuth } from './AuthContext';
import { useDrop } from 'react-dnd';

function TaskList() {
  const { tasks, setTasks } = useContext(TaskContext);
  const { user } = useAuth();

  const userTasks = tasks.filter(task => task.assigned_users.some(assignedUser => assignedUser.id === user.id));

  const handleDrop = (draggedId, targetIndex) => {
    const updatedTasks = [...userTasks];
    const draggedTask = updatedTasks.find(task => task.id === draggedId);
    const draggedIndex = updatedTasks.indexOf(draggedTask);

    if (targetIndex > draggedIndex) {
      updatedTasks.splice(targetIndex + 1, 0, draggedTask);
      updatedTasks.splice(draggedIndex, 1);
    } else {
      updatedTasks.splice(targetIndex, 0, draggedTask);
      updatedTasks.splice(draggedIndex + 1, 1);
    }

    setTasks(updatedTasks);
  };

  const [, drop] = useDrop({
    accept: 'TASK',
    drop: (item, monitor) => {
      const draggedId = item.id;
      const targetIndex = userTasks.length; // Drop at the end of the list
      handleDrop(draggedId, targetIndex);
    },
    collect: monitor => ({
      isOver: !!monitor.isOver(),
    }),
  });

  return (
    <div ref={drop} style={{ minHeight: '100vh', background: 'lightgray', padding: '20px' }}>
      {userTasks.map((task, index) => (
        <Task
          key={task.id}
          index={index}
          task={task}
          sourceSection={task.section}
          isAdmin={user.role === 'admin'}
        />
      ))}
    </div>
  );
}

export default TaskList;
