import React, { useState, useEffect } from 'react';
import Task from './Task';

function TaskList({ onDrop }) {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleDrop = (index, task) => {
    onDrop(index, task);
  };

  const fetchTasks = async () => {
    try {
      const response = await fetch('http://localhost:5555/api/tasks');
      if (!response.ok) {
        throw new Error('Failed to fetch tasks');
      }
      const data = await response.json();
      setTasks(data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const moveTask = (dragIndex, hoverIndex, task) => {
    const draggedTask = tasks[dragIndex];
    const updatedTasks = [...tasks];
    updatedTasks.splice(dragIndex, 1);
    updatedTasks.splice(hoverIndex, 0, draggedTask);
    setTasks(updatedTasks);
  };

  return (
    <div>
      {tasks.map((task, index) => (
        <Task key={task.id} index={index} task={task} onDrop={handleDrop} moveTask={moveTask} />
      ))}
    </div>
  );
}

export default TaskList;
