import React, { useContext } from 'react';
import Task from './Task';
import TaskContext from './TaskContext'; // Import TaskContext

function DelayedTaskList({ onDrop }) {
  const { tasks } = useContext(TaskContext); // Access tasks from TaskContext

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedTask = JSON.parse(e.dataTransfer.getData('task'));
    onDrop(droppedTask);
  };

  const allowDrop = (e) => {
    e.preventDefault();
  };

  return (
    <div onDrop={handleDrop} onDragOver={allowDrop}>
      {tasks.map((task, index) => (
        <Task key={task.id} index={index} task={task} />
      ))}
    </div>
  );
}

export default DelayedTaskList;
