// TaskList.js
import React, { useContext } from 'react';
import Task from './Task';
import TaskContext from './TaskContext';

function TaskList({ onDrop }) {
  const { tasks } = useContext(TaskContext); // Access tasks from TaskContext

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedTask = JSON.parse(e.dataTransfer.getData('task'));
    onDrop(droppedTask);
  };

  const allowDrop = (e) => {
    e.preventDefault();
  };

  return (
    <div onDrop={handleDrop} onDragOver={allowDrop}>
      {tasks.map((task, index) => (
        <Task key={task.id} index={index} task={task} isAdmin={user.role === 'admin'} />
      ))}
    </div>
  );

}

export default TaskList;
